<?phpclass AccessDB{		private $USER = "root";		private $PW   = "root";		private $dnsinfo = "mysql:dbname=goods;host=localhost;charset=utf8";		private $pdo;		public function __construct() {				try{						$this->pdo = new PDO($this->dnsinfo,$this->USER,$this->PW);						$this->pdo->setAttribute( PDO::ATTR_EMULATE_PREPARES, false );						return $this->pdo;				}catch(PDOException $e){						die( 'Error:' . $e->getMessage());				}		}}?>