<?php//クラスインポートrequire_once "../Exception/ModelException.php";class AccessDB{		private $connection;		private $res;		//以下定数につき変更拒否		private $USER = "root";		private $PW   = "root";		private $DNSINFO = "mysql:dbname=goods;host=localhost;charset=utf8";		public function __construct() {				try{						$this -> connection = new PDO($this -> DNSINFO, $this -> USER, $this -> PW);						$this -> connection->setAttribute(PDO::ATTR_EMULATE_PREPARES, false);						$this -> res = TRUE;				}catch(PDOException $e){						$this -> res = FALSE;						throw new ModelException("S001");				}		}		public function getCon(){			return $this -> connection;		}		public function getRes(){			return $this -> res;		}}?>