<?phpclass AccessDBModel{	//フィールド	private $pdo;	private $res;	//コンストラクタ	public function __construct(){		try{			//接続準備			$USER = "root";			$PW   = "root";			$DB   = "goods";			$HOST = "localhost";			$dnsinfo = "mysql:dbname={$DB};host={$HOST};charset=utf8";			//DB接続			$this -> pdo = new PDO($dnsinfo,$USER,$PW);			//接続成功で1を返す			$this -> res = "1";		}catch(PDOException $e){			$this -> res = $e -> getMessage();		}	}	//getter	public function getPdo(){		return $this -> pdo;	}	public function getRes(){		return $this -> res;	}}?>