<?phpclass SelectGoodsModel{	//フィールド	private $goods;	private $pdo;	private $res;	//コンストラクタ	public function __construct(){		//DBアクセス		require_once 'AccessDBModel.php';		$ad= new AccessDBModel();		$this -> pdo = $ad -> getPdo();		$this -> res = $ad -> getRes();		//データ格納用配列初期化		$this -> goods = array();	}	//クエリ実行	function executeSelect($sql) {		try{			//クエリ実行			$stmt = $this ->  pdo -> prepare($sql);			$this -> res = $stmt -> execute(null);			//データベースのテーブル取得			$i = 0;			while($row = $stmt -> fetch(PDO::FETCH_ASSOC)){				//1行ずつ配列に追加				$this -> result[$i] = $row;				$i++;			}		}catch (PDOExeption $e){			$this -> res = $e -> getMassage();		}		//データベースのテーブル返す		return $this -> goods;	}}?>