<?php	//商品一覧取得	require_once '/Controller/SelectGoodsController.php';	//フォーム作成クラス	require_once '/Controller/CreateFormTag.php';	//フォームタグ作成クラスインスタンス	$cft = new CreateFormTag();	//商品取得クラスのインスタンス	$sgc = new SelectGoodsController();	//データベースのテーブル取得	$foods = $sgc -> getFoods();	$drinks = $sgc -> getDrinks();?><!DOCTYPE html><html><head><meta charset="UTF-8"><title>注文システム</title><script src="OrderSystem.js"></script></head><body onLoad="onLoad()">	<p>注文メニュー</p>	<form method="post" action="JavaScript/Confirm.php">		<table>			<?php				//データベースから受け取ったレコードからフォームを作成				//食べ物				foreach ($cft -> createFormTag($foods) as $val){					echo $val;				}				//飲み物				foreach ($cft -> createFormTag($drinks) as $val){					echo $val;				}				?>		</table>		<p>合計金額　<span id="total">0</span>円</p>		<input type="hidden" name="total" id ="postTotal">		<input type="submit" value="注文" name="submit">	</form></body></html>